const inputItems = $input.all();

const grouped = {};

for (const item of inputItems) {
  const normalized = {};
  for (const key in item.json) {
    normalized[key.toLowerCase()] = item.json[key];
  }

  const {
    file_location,
    file_name,
    line,
    message,
    type,
    output_location,
    test_location,
    test_file_name
  } = normalized;

  const key = `${file_location}||${file_name}`;

  if (!grouped[key]) {
    grouped[key] = {
      file_Location: file_location,
      file_name,
      output_location,
      test_location,
      test_file_name,
      issues: []
    };
  }

  grouped[key].issues.push({ line, message, type });
}

const result = [];

for (const key in grouped) {
  const {
    file_Location,
    file_name,
    output_location,
    test_location,
    test_file_name,
    issues
  } = grouped[key];

  const issueDescriptions = issues
    .map((issue, index) => {
      return `Issue ${index + 1}: The detected "${issue.type}" issue by SonarQube is on line: ${issue.line}. Description of how to solve this code issue: "${issue.message}".`;
    })
    .join("\n");

  result.push({
    json: {
      file_Location,
      file_name,
      output_location,
      test_location: test_location || "",
      test_file_name: test_file_name || "",
      issueSummary: issueDescriptions,
      totalIssues: issues.length
    }
  });
}

return result;

const testLocation = $('Loop to Create Test Cases1').item.json.test_location;

