const buckets = {};

for (const item of items) {
  const row = item.json;
  const { file_name } = row;
  if (!file_name) continue; // skip rows missing file_name
  if (!buckets[file_name]) buckets[file_name] = [];
  buckets[file_name].push(row);
}

return Object.entries(buckets).map(([file_name, rows]) => ({
  json: {
    file_name,
    rows,          // all CSV rows that shared this file_name
    count: rows.length
  }
}));
